/*Given a string s, find the length of the longest
substring
without repeating characters.

Example 1:

Input: s = "abcabcbb"
Output: 3
Explanation: The answer is "abc", with the length of 3.

Example 2:

Input: s = "bbbbb"
Output: 1
Explanation: The answer is "b", with the length of 1.

Example 3:

Input: s = "pwkew"
Output: 3
Explanation: The answer is "wke", with the length of 3.
Notice that the answer must be a substring, "pwke" is a subsequence and not a substring.
*/
#include <iostream>
#include <string> 
#include <unordered_map>

using namespace std;

int longest(string s)
{
    if(s.length() == 0) return 0;
    if(s.length() == 1) return 1;
    unordered_map<char, int> substring;
    int count = 0; 
    int current_longest = 0;
    char last_char = '\0';
   for(int i=0; i<s.length(); i++)
    {
        if( s[i] != last_char && substring.find(s[i])==substring.end()){
            count++;
            last_char = s[i];
            substring[last_char] = count;
        }
        else
        {
            count = 0;
            last_char = '\0';
            i = i-1; // i can't be negative because we've seen a our character before
            substring.clear();
        }

        if( count > current_longest ) current_longest = count;

    }
    return current_longest;
}

int lengthOfLongestSubstring(string s)
{
    int current_longest = 0;
    int count = 0;
    for(int i = 0; i < 256; i++)
    { 
        count = longest(s.substr(i));
        if(count > current_longest) current_longest = count; 
        if( current_longest > s.substr(i).length() ) break;
    }
    return current_longest;
}
 
